call insere_a_direita_dos_filhos('lixeira', 'item_3_lista_reciclagem', '(iii) Pontos de  Cultura e', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
